mapper.select:
SELECT c.oid AS dtuId, c.Address AS dtuAddress, c.`Name` AS dtuName, c. PORT AS dtuPort, d.TerminalId AS terminalId, IFNULL(d.protocolType, NULL) AS dtuType FROM device_dtu c LEFT JOIN device_terminal d ON d.TerminalId = c.TerminalId WHERE 1 = 1
AND c.PORT IN #{item}
AND c.Oid IN #{item}

SELECT c.oid AS dtuId, c.Address AS dtuAddress, a.oid AS deviceId, a. NAME AS deviceName, a.Address AS deviceAddress, IFNULL(d.protocolType, NULL) AS deviceType, d.TerminalId AS terminalId, 'device_rcd' AS deviceTableName, a.daId, IFNULL(a. LEVEL, 1) AS deviceLevel FROM device_rcd a LEFT JOIN device_dtu c ON c.oid = a.DtuId LEFT JOIN device_terminal d ON d.TerminalId = a.TerminalId WHERE 1 = 1
AND c.PORT IN #{item}
AND a.Oid IN #{item}
UNION
SELECT c.oid AS dtuId, c.Address AS dtuAddress, a.oid AS deviceId, a. NAME AS deviceName, a.Address AS deviceAddress, IFNULL(d.protocolType, NULL) AS deviceType, d.TerminalId AS terminalId, 'device_temperature' AS deviceTableName, a.daId, 1 FROM device_temperature a LEFT JOIN device_dtu c ON c.oid = a.DtuId LEFT JOIN device_terminal d ON d.TerminalId = a.TerminalId WHERE 1 = 1
AND c.PORT IN #{item}
AND a.Oid IN #{item}
UNION
SELECT c.oid AS dtuId, c.Address AS dtuAddress, a.oid AS deviceId, a. NAME AS deviceName, a.Address AS deviceAddress, IFNULL(d.protocolType, NULL) AS deviceType, d.TerminalId AS terminalId, 'device_fallingtypeswitch' AS deviceTableName, a.daId, 1 FROM device_fallingtypeswitch a LEFT JOIN device_dtu c ON c.oid = a.DtuId LEFT JOIN device_terminal d ON d.TerminalId = a.TerminalId WHERE 1 = 1
AND c.PORT IN #{item}
AND a.Oid IN #{item}
